                    @click="activeKey = 2">每月</div>
                </div>
            </div>
        </div>
    </div>
</template>


<script setup>
import Header from '../../layouts/Header.vue';
import LIneChat from './components/LIneChat.vue';
import { getSubNumReq, getNewsTimeReq, getNewsPubDataReq } from '../../api/news';
import { computed, onMounted, ref } from 'vue';
import useCategoryStore from '../../store/modules/useCategoryStore';
import { calculateTimeDifference } from '@/utils/time'

const numData = ref({})
const maxKey = ref()
const minKey = ref()
const categoryStore = useCategoryStore()
const timer = ref('')
const activeKey = ref(0)
const chartsData = ref({})

const getNums = async () => {
    const res = await getSubNumReq();
    if (res.code == 0) {
        numData.value = res.data;
        const vals = Object.values(res.data);
        const maxVal = Math.max(...vals);
        const minVal = Math.min(...vals);
        maxKey.value = Object.keys(res.data).find(key => res.data[key] === maxVal).split('Num')[0];
        minKey.value = Object.keys(res.data).find(key => res.data[key] === minVal).split('Num')[0];
    }
}



const calculate = (startTime) => {
    const diff = calculateTimeDifference(startTime)
    timer.value = diff.days + '天' + diff.hours + '时' + diff.minutes + '分' + diff.seconds + '秒'
}

const getChartsData = async () => {
    const res = await getNewsPubDataReq();
    if (res.code == 0) {
        chartsData.value = res.data;
    }
}

const formatData = computed(() => {
    if (Object.keys(chartsData.value).length == 0) {
        return []
    }
    const maps = Object.keys(chartsData.value)
    const keys = ['dayTime', 'weekTime', 'monthTime']
    const dataKeys = Object.keys(chartsData.value['dayMap'])
    const resKeys = chartsData.value[maps[activeKey.value]]['geo'].map(item => item[keys[activeKey.value]])
    const resValue = dataKeys.map(item => {
        const dt = chartsData.value[maps[activeKey.value]][item]
        const data = dt.map(ie => ie.value)
        return {
            name: item,
            type: 'line',
            stack: 'Total',
            data
        }
    })
    return {resKeys, resValue, maps}
})


onMounted(async () => {
    getNums();
    const { data: { startTime } } = await getNewsTimeReq()
    console.log(startTime)
    setInterval(() => {
        calculate(startTime);
    }, 1000)
    await getChartsData()
})

</script>


